import axios from 'axios';
import React, { useContext, useState } from 'react';
import { AuthContext } from '../../components/context';
import { useNavigate } from 'react-router-dom';
import cl from "./LoginPage.module.css"

interface formData {
  username: string,
  password: string,
}

interface formResponse {
  data: {
    access_token: string,
    refreshToken: string,
  }
  }


const LoginPage = () => {
  const navigate = useNavigate()
  const { isAuth, setIsAuth } = useContext<any>(AuthContext);
  const username = useState("")
  const password = useState("")
  const exitFromAccount = (e: Ev) => {
    e.preventDefault();
    setIsAuth(false);
    localStorage.removeItem("auth");
    localStorage.removeItem("accessToken");
    localStorage.removeItem("userId");
    localStorage.removeItem("is_specialist");
    delete axios.defaults.headers.common['Authorization'];
    navigate("/login");
  }


 const [userData, setUserData] = useState<formData>(
         {
             username: "",
             password: ""
         }
     )

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
          const {name, value} = e.target;
          setUserData(prev => ({
              ...prev,
              [name]: value
          }))
      }


  const loginFormSend = async () => {
    try {
      const response: formResponse = await axios.post<formData>("/v1/auth/login/", userData, {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        }
      })
      const accessToken = response.data.access_token
      if (accessToken) {
        console.log("Вход успешно совершен");
        setIsAuth(true);
        localStorage.setItem("auth", "true");
        localStorage.setItem("accessToken", accessToken);
        navigate("/");
      }
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <div className={cl.background}>
      <div className={cl.container}>
        <div className={cl.header}>
          <h1 className={cl.title}>Добро пожаловать</h1>
          <p className={cl.subtitle}>Введите ваши данные для входа</p>
        </div>

        <form onSubmit={loginFormSend} className={cl.form}>
          <div className={cl.inputGroup}>
            <label htmlFor="login" className={cl.label}>Логин</label>
            <input
              id="login"
              className={cl.input}
              value={userData.username}
              onChange={handleChange}
              type="text"
              placeholder="Введите ваш логин"
              required
            />
          </div>

          <div className={cl.inputGroup}>
            <label htmlFor="password" className={cl.label}>Пароль</label>
            <input
              id="password"
              className={cl.input}
              value={userData.password}
              onChange={handleChange}
              type="password"
              placeholder="Введите ваш пароль"
              required
            />
          </div>



          <button 
            type="submit" 
            className={cl.button}
          >

          </button>
        </form>

        {isAuth && (
          <button 
            onClick={exitFromAccount} 
            className={`${cl.button} ${cl.exitButton}`}
          >
            Выйти из аккаунта
          </button>
        )}

        <div className={cl.footer}>
          <p className={cl.footerText}>Нет аккаунта? <a href="/register" className={cl.link}>Зарегистрируйтесь</a></p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
