// import 'bootstrap/dist/css/bootstrap.min.css'; 


interface Product {
  id: string,
  item_id: number,
  name: string,
  cost: number,
  description: string,
}
const MainPage: FC = () => {
  const [cart, setCart] = useState<Product[]>([
    { id: "2", item_id: 1, description: ' 50', name: '–ü–æ–∏—Å–∫', cost: 12 },
    { id: "2", item_id: 2, description: '‚ù§Ô∏è', name: '–õ–∞–π–∫', cost: 122 },
    { id: "2", item_id: 3, description: 'üìß', name: 'Email', cost: 212 },
    { id: "2", item_id: 4, description: '‚öôÔ∏è', name: '–ù–∞—Å—Ç—Ä–æ–π–∫–∏', cost: 33 },
    { id: "2", item_id: 5, description: 'üìÅ', name: '–ü–∞–ø–∫–∞', cost: 333 },
  ]);



  const buyProd = async (itemId: number) => {
    try {

      const response = await axios.post<string>("/api/v1/game/buy/", {itemId:itemId })
      console.log(`–ø–æ–∫—É–ø–∞–µ–º ${itemId}`);

      setCart(prevCart => prevCart.filter(item => item.item_id !== itemId))

    } catch (error) {
      alert("–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–∫—É–ø–∫–µ")
      console.log(error);

    }

  }



  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/v1/game/shop/");
        setCart(response.data)
      } catch (error) {
        console.log(error);
      }
    }

    fetchProducts();
  }, [cart])

  return (

    <div className={cl.main}>
      <section className={cl.header}>
        <div className={cl.container}>
          <img className={cl.header__image} src={head} alt="" />
        </div>
      </section>
      <section className={cl.products}>
        <div className={cl.container}>
          {cart.map((prod) => (
            <div
              key={prod.item_id}
              className={`${cl.prodItem}`}
            >
              <div className={cl.prodItem__info}>
                <span>
                  {prod.name}
                </span>
                <img className={cl.prodItem__img} src={def} />
                <div className={cl.prodItem__text}>
                  <span>
                    {prod.description}
                  </span>
                  <span>
                    –¶–µ–Ω–∞: {prod.cost} Zcoins
                  </span>
                </div>

              </div>
              <button
                className={cl.prodItem__button}
                onClick={() => buyProd(prod.item_id)}
              >
                –ö—É–ø–∏—Ç—å
              </button>
            </div>
          ))}
        </div>

      </section>

    </div>


  )
}

