import React, { useState } from 'react';
import cl from "./RegPage.module.css"

interface formD {
    email: string,
    username: string,
    password: string
}

const RegPage = () => {
    const [formData, setFormData] = useState<formD>(
        {
            email: "",
            username: "",
            password: ""
        }
    )

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const {name, value} = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }))
    }

    return (
        <div className={cl.background}>
            <div className={cl.container}>
                <div className={cl.header}>
                    <p className={cl.subtitle}></p>
                </div>
                <form action="" className={cl.form}>
                    <div className={cl.formGrid}>
                        <div className={cl.inputGroup}>
                            <label htmlFor="username" className={cl.label}>username*</label>
                            <input
                            id="username"
                             name="username"
                             className={cl.input}
                             value={formData.username}
                             onChange={handleChange}
                             type="text"
                             placeholder="Ваше имя"
                             required
                             />
                        </div>
                    </div>
                </form>


            </div>
        </div>
    );
};

export default RegPage;

